import { addItem } from "../utils/cartSlice";
import { CDN_IMG } from "../utils/constants";
const ItemList = ({itemList}) => {
    const {itemCards} = itemList;
    // console.log(itemList);

    const handleItems = () =>{
        dispatch(addItem("pizza"));
    }

    return(
        <div>
            <ul>
                {itemCards.map((items)=>(
                    <li key={items?.card?.info?.id}>
                        <div className="font-semibold text-left">
                        {items?.card?.info?.name}
                        <img className="content-end w-20 h-20 rounded-lg" alt="res-logo" src={ CDN_IMG + items?.card?.info?.imageId}></img>
                        <span onClick={handleItems} className="rounded-lg p-2 text-white bg-black">add+</span>
                        </div> 
                        <div className="font-semibold text-left"> â‚¹
                        {items?.card?.info?.price/100 || items?.card?.info?.defaultPrice/100}
                        </div> 
                        <div className="font-extralight text-left">
                            {items?.card?.info?.description}
                        </div>
                        </li>
                ))}
            </ul>
        </div>
    )
}

export default ItemList;
