         window.addEventListener("online", () => {
            console.log("You are now connected to the network.");
            // setOnlineStatus(true);
          });
          window.addEventListener("offline", () => {
            console.log("You are now connected to the network.");
            // setOnlineStatus(true);
          });
          window.ononline = (event) => {
            console.log("You are now connected to the network.");
          };
    // },[])

