import { useEffect } from "react";

const useOnlineStatus = () =>{
    const onlineStatus = false;
    useEffect(()=>{
        window.addEventListener("online", (event) => {
            console.log("You are now connected to the network.",event);
          });
    },[])

    return onlineStatus;
}
export default useOnlineStatus;
