import { useEffect, useState } from "react";

import { useParams } from "react-router-dom";
import useRestaurantData from "./useRestaurantData";

const RestaurantPage = () => {
    const resId = useParams();
    console.log(resId.id);
    const [restoData,getRestoData] = useState(null);


    useEffect(() => {
        MenuData()
    },
    []);

    const MenuData = async () => {
        const data = await fetch("https://www.swiggy.com/dapi/menu/pl?page-type=REGULAR_MENU&complete-menu=true&lat=12.9698196&lng=77.7499721&restaurantId="+resId.id);
        const jsonData = await data.json();
        console.log(jsonData?.cards[2]?.groupedCard?.cardGroupMap?.REGULAR.cards[2]?.card?.card?.itemCards);
        getRestoData(jsonData);
        // useResInfo(jsonData?.data);
    };

    // getRestoData(useRestaurantData(resId.id)?.cards[2]?.groupedCard?.cardGroupMap?.REGULAR.cards[2]?.card?.card?.itemCards);
    // console.log(useRestaurantData(resId.id)?.cards[2]?.groupedCard?.cardGroupMap?.REGULAR.cards[2]?.card?.card?.itemCards);
    // if(restoData===null){
    //     return (<h1>hello</h1>)
    // }
    return (
        <div>
        {restoData==null ? (<div><h2>Loading.............</h2></div>) : 
        (<div>
            <ul>
            {restoData.map((item) => (
                <li key={item.card?.info?.id}>
                    {item.card?.info?.name} - Rs {item.card?.info?.price/100}
                </li>))} 
            </ul>
        </div>)}
        </div>
    )
}

export default RestaurantPage;
