import ItemList from "./ItemList";
import { useState } from "react";
const ItemCard = ({items}) =>{
    const { title , id} = items;
    console.log(id)
    const[showItems,setShowItems] = useState(true);
    return(
        <div>
            <li key={id} onClick={() => (setShowItems(!showItems))} className="font-bold text-centre p-5 hover:bg-slate-100">
                    {title} ⬇️
            </li>
            {/* {title} */}
            {showItems && <ItemList itemList={items}/>}
        </div>
    )
}

export default ItemCard;
