import RestaurantCard from "./RestaurantCard";
import resData from "../utils/mockData";
import React, { useState, useEffect } from 'react'; 
import Shimmer from "./shimmer";
import { Link } from "react-router-dom";

// console.log(resData);

const Body = () => {
    const [Data, setData] = useState([]);
    const [filteredData, setFilteredData] = useState([]);

    const[searchText, setSearchText] = useState("");

    console.log("rendered",searchText);

    useEffect( () => {
        fetchData();
        console.log("useEffect called");
    },[searchText])

    const fetchData = async () =>  {
        const data = await fetch("https://www.swiggy.com/dapi/restaurants/list/v5?lat=12.9698196&lng=77.7499721&is-seo-homepage-enabled=true&page_type=DESKTOP_WEB_LISTING")

        const json = await data.json();

        console.log(json.data.cards[2].card.card.gridElements)

        setData(json?.data?.cards[2]?.card?.card?.gridElements?.infoWithStyle?.restaurants);
        setFilteredData(json?.data?.cards[2]?.card?.card?.gridElements?.infoWithStyle?.restaurants);
    };

    // if(Data.length===0){
    //     return (
    //     <div className="shimmer">
    //         <Shimmer/>
    //     </div>
    //     )
    //     // <h1>Loading.......!</h1>)
    // }
    return (
        <div className="body">
            {/* <div className="search">Search</div> */}
            <div className="search-bar">
            <button className="filter-btn"
                onClick={() =>  {

                    const filteredData = Data.filter((e) => e.info.avgRating>4.0);
                    setData(filteredData);
                    // console.log(Data);
                }
                }
            >Filter by ratings</button>


                <input type="search" className="search-box" value={searchText}  onChange={(e) => setSearchText(e.target.value)}></input>
                <button onClick={() => {
                    // console.log(searchText);
                    // console.log(Data)
                    // Data.forEach(element => {
                    //     console.log(element.info.name);
                    // });

                    const filterData = Data.filter((element) => element.info.name.toLowerCase().includes(searchText.toLowerCase()));
                    console.log(filterData);
                    setFilteredData(filterData);

                    // console.log(Data.info.name)
                    // if(Data.info.name.includes(searchText)){

                    // }
                }
                }>search</button>
            </div> 

            <div className="resto-container">
                {filteredData.map((restaurant) => (
                <Link to={"restaurant/"+restaurant.info.id}><RestaurantCard key={restaurant.info.id} restoData = {restaurant.info}/></Link>))}
                {/* {dummtyData.map((restaurant) => (<RestaurantCard key={restaurant.id} restoData = {restaurant}/>))} */}
            </div>
        </div>
    )
}

export default Body;
