import { useParams } from "react-router-dom";
import useRestaurantData from "../utils/useRestaurantData";
import ItemList from "./ItemList";

const RestaurantPage = () => {
    const resId = useParams();
    const restoData = useRestaurantData(resId.id);
    console.log("restodata",restoData);

    const[showItems,setShowItems] = useState(true);



    // if(restoData!=null){
    const category = restoData?.filter((category)=>{
        return category?.card?.card?.["@type"] == "type.googleapis.com/swiggy.presentation.food.v2.ItemCategory"
    })
    console.log("category",category);
// }
    return (
        // <div><h1>work in progress</h1></div>
        <div>
        {(category==null || category == undefined) ? (<div><h2>Loading.............</h2></div>) :
        (<div>
            <ul className="">
            {category.map((c) => (
                <li className="w-6/12  mx-auto text-center px-20 mx-20 border-b-2"key={c.card?.card?.title}>
                    {/* {item.card?.info?.name} - Rs {item.card?.info?.price/100} */}
                    <div onClick={() => (setShowItems(!showItems))} className="font-bold text-left">
                    {c?.card?.card?.title} ⬇️
                    </div>

                    {showItems && <ItemList items={c?.card?.card?.itemCards}/>}

                    {/* {showItems && (<div>
                    <ul>
                    {c?.card?.card?.itemCards?.map((items)=>(
                        <li key={items?.card?.info?.id}>
                        <div className="font-semibold text-left">
                        {items?.card?.info?.name}
                        </div> 
                        <div className="font-semibold text-left"> ₹
                        {items?.card?.info?.price/100}
                        </div> 
                        <div className="font-extralight text-left">
                            {items?.card?.info?.description}
                        </div>
                        </li>

                    ))}
                    </ul>
                    </div>)} */}
                </li>))} 
            </ul>
        </div>)}
        </div>
    )
}

export default RestaurantPage;
