    // console.log(useRestaurantData(resId.id)?.cards[2]?.groupedCard?.cardGroupMap?.REGULAR.cards[2]?.card?.card?.itemCards);
    // if(restoData===null){
    //     return (<h1>hello</h1>)
    // }
    return (
        <div>
        {restoData==null ? (<div><h2>Loading.............</h2></div>) : 
        (<div>
            <ul>
            {restoData.map((item) => (
                <li key={item.card?.info?.id}>
                    {item.card?.info?.name} - Rs {item.card?.info?.price/100}
                </li>))} 
            </ul>
        </div>)}
        </div>
    )
}

export default RestaurantPage;
