import ItemList from "./ItemList";
import { useState } from "react";
const ItemCard = ({items, showItem}) =>{
    const { title } = items;
    // const[showItems,setShowItems] = useState(false);
    return(
        <div>
            <li onClick={() => (setShowItems(!showItems))} className="font-bold text-centre p-5 cursor-pointer hover:bg-slate-100">
                    {title} ⬇️
            </li>
            {showItem && <ItemList itemList={items}/>}
        </div>
    )
}

export default ItemCard;
